import axios from 'axios';
export default {
  data() {
    return {
      name_show: false,
      cname_show: false,
      name: '',
      cname: '',
      reg_no: '',
      c_no: '',
      mark: ''
    };
  },

  methods: {
    async getName() {
      axios.get(`http://localhost:8068/student/detail/${this.reg_no}`).then(res => {
        console.log(res.data);

        if (res.data.length == 0) {
          alert("Invalid Register Number");
          this.reg_no = '';
          return;
        }

        this.name = res.data[0].name;
        this.name_show = true;
      }).catch(err => {
        console.log(err);
      });
    },

    async getCourse() {
      axios.get(`http://localhost:8068/course/detail/${this.c_no}`).then(res => {
        console.log(res.data);

        if (res.data.length == 0) {
          alert("Invalid Course Number");
          this.c_no = '';
          return;
        }

        this.cname = res.data[0].name;
        this.cname_show = true;
      }).catch(err => {
        console.log(err);
      });
    }

  }
};
